import { css, jsx, Global } from '@emotion/core'
import Link from "next/link"
import Layout from '../components/Layout';


const channel = ({ channel, audioClips, series }) => {

  const bodyStyle = css`
		body {
			width: 100%;
			margin: 0;
			font-family: system-ui;
		}
	`;

  const styles = css`
    & h1 {
      font-weight: 600;
      padding: 15px;
    }

	`;
  return(
    <Layout>
      <div css={styles}>
        <Global
          styles={bodyStyle}
        />
        <h1>{ channel.title }</h1>

        <h2>Series</h2>
        {series.map( serie => (
          <div> { serie.title } </div>
        ))}

        <h2>Últimos Podcasts</h2>

        {audioClips.map( clip => (
          <Link href={`/podcast?id=${clip.id}`}>
            <a> { clip.title } </a>
          </Link>
        ))}
      </div>
    </Layout>
  )
}

export async function getServerSideProps({ query }) {

  let channelId = query.id;

  let [ reqChannel, reqAudioClips, reqSeries ] = await Promise.all([
    fetch(`https://api.audioboom.com/channels/${channelId}`),
    fetch(`https://api.audioboom.com/channels/${channelId}/audio_clips`),
    fetch(`https://api.audioboom.com/channels/${channelId}/child_channels`)
  ])

  let dataChannel = await reqChannel.json();
  let channel = dataChannel.body.channel

  let dataAudioClips = await reqAudioClips.json();
  let audioClips = dataAudioClips.body.audio_clips

  let dataSeries = await reqSeries.json();
  let series = dataSeries.body.channels

	return { props: { channel, audioClips, series } }
}

export default channel;
