import { css, jsx } from '@emotion/core'

const  About = () => {
	const containerStyle = css`
		width: 100%;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;

		& img {
			width: 100%;
		}
	`;
	return( 
		<div css={containerStyle}>
			<img src="/static/platzi-logo.png" alt="Platzi Logo" />
		</div>
	)
}

export default About
