import Link from "next/link";
import slugify from '../helpers/slug';

const PodcastList = ({ audioClips, setOpenPodcast }) => {
  return (
    audioClips.map( clip => (
      <Link router="podcast"
        params={{
          slugChannel: slugify(clip.channel.title),
          idChannel: clip.channel.id,
          slug: slugify(clip.title),
          id: clip.id
        }}
      >
        <a onClick={e => setOpenPodcast(e, clip)}> { clip.title } </a>
      </Link>
    ))
  )
}

export default PodcastList;
