import { css, jsx, Global } from '@emotion/core'


const channel = ({ channel, audioClips, series }) => {

  const bodyStyle = css`
		body {
			width: 100%;
			margin: 0;
			font-family: system-ui;
		}
	`;

  const styles = css`
		& header {
			color: #fff;
			background: #8756ca;
			padding: 15px;
			text-align: center;
    }

    & h1 {
      font-weight: 600;
      padding: 15px;
    }

	`;
  return(
    <div css={styles}>
      <Global
				styles={bodyStyle}
			/>
      <header>Podcasts</header>
      <h1>{ channel.title }</h1>

      <h2>Series</h2>
      {series.map( serie => (
        <div> { serie.title } </div>
      ))}

      <h2>Ãšltimos Podcasts</h2>

      {audioClips.map( clip => (
        <div> { clip.title } </div>
      ))}
    </div>
  )
}

export async function getServerSideProps({ query }) {

  let channelId = query.id;
	let reqChannel = await fetch(`https://api.audioboom.com/channels/${channelId}`);
  let dataChannel = await reqChannel.json();
  let channel = dataChannel.body.channel

  let reqAudioClips = await fetch(`https://api.audioboom.com/channels/${channelId}/audio_clips`);
  let dataAudioClips = await reqAudioClips.json();
  let audioClips = dataAudioClips.body.audio_clips

  let reqSeries = await fetch(`https://api.audioboom.com/channels/${channelId}/child_channels`);
  let dataSeries = await reqSeries.json();
  let series = dataSeries.body.channels

	return { props: { channel, audioClips, series } }
}

export default channel;
