import { css, jsx, Global } from '@emotion/core'

const Podcast = ({ podcast }) => {

  const bodyStyle = css`
  body {
    width: 100%;
    margin: 0;
    font-family: system-ui;
  }
`;

  const styles = css`
  nav {
    background: none;
  }
  nav a {
    display: inline-block;
    padding: 15px;
    color: white;
    cursor: pointer;
    text-decoration: none;
  }
  .clip {
    display: flex;
    height: 100%;
    flex-direction: column;
    background: #8756ca;
    color: white;
  }
  picture {
    display: flex;
    align-items: center;
    justify-content: center;
    flex: 1 1;
    flex-direction: column;
    width: auto;
    padding: 10%;
  }
  picture div {
    width: 100%;
    height: 100%;
    background-position: 50% 50%;
    background-size: contain;
    background-repeat: no-repeat;
  }
  .player {
    padding: 30px;
    background: rgba(0,0,0,0.3);
    text-align: center;
  }
  h3 {
    margin: 0;
  }
  h6 {
    margin: 0;
    margin-top: 1em;
  }
  audio {
    margin-top: 2em;
    width: 100%;
  }

  .modal {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 99999;
  }
  `;


  return (
    <div css={styles}>
      <Global
				styles={bodyStyle}
			/>
      <header>Podcasts</header>
  <h1>{podcast.title}</h1>
    </div>
  )
}

export async function getServerSideProps({query}){
  let podcastId = query.id;

  let reqPodcast = fetch(`https://api.audioboom.com/audio_clips/${podcastId}.mp3`)

  let dataPodcast = await reqPodcast.json();
  let podcast = dataPodcast.body.audio_clip

  return { props: { podcast }}
}



export default Podcast
